import React, { Component } from 'react';
import Web3 from 'web3'
import MemoryToken from '../abis/MemoryToken.json'
import './App.css';

const CARD_ARRAY = [
  {
    name: 'fries',
    img: '/images/fries.png'
  },
  {
    name: 'cheeseburger',
    img: '/images/cheeseburger.png'
  },
  {
    name: 'ice-cream',
    img: '/images/ice-cream.png'
  },
  {
    name: 'pizza',
    img: '/images/pizza.png'
  },
  {
    name: 'milkshake',
    img: '/images/milkshake.png'
  },
  {
    name: 'hotdog',
    img: '/images/hotdog.png'
  },
  {
    name: 'fries',
    img: '/images/fries.png'
  },
  {
    name: 'cheeseburger',
    img: '/images/cheeseburger.png'
  },
  {
    name: 'ice-cream',
    img: '/images/ice-cream.png'
  },
  {
    name: 'pizza',
    img: '/images/pizza.png'
  },
  {
    name: 'milkshake',
    img: '/images/milkshake.png'
  },
  {
    name: 'hotdog',
    img: '/images/hotdog.png'
  }
]


class App extends Component {
  async componentWillMount() {
    await this.loadWeb3()
    await this.loadBlockchainData()
    this.setState({cardArray: CARD_ARRAY.sort(()=>0.5-Math.random())})
  }

  async loadBlockchainData() {
    const web3 = window.web3
    const accounts = await web3.eth.getAccounts()
    this.setState({ account: accounts[0] })
    const networkId = await web3.eth.net.getId()

    // Load MemoryToken.sol
    const memoryTokenData = MemoryToken.networks[networkId]
    if(memoryTokenData) {
      const memoryToken = new web3.eth.Contract(MemoryToken.abi, memoryTokenData.address)
      this.setState({ memoryToken })
      let totalSupply = await memoryToken.methods.totalSupply().call()
      this.setState({totalSupply: totalSupply.toString() })

      // Load Tokens
      let balanceOf = await memoryToken.methods.balanceOf(accounts[0]).call()
      for (let i = 0; i < balanceOf; i++) {
        let id = await memoryToken.methods.tokenOfOwnerByIndex(accounts[0], i).call()
        let tokenURI = await memoryToken.methods.tokenURI(id).call()
        this.setState({
          tokenURIs: [...this.state.tokenURIs, tokenURI]
        })
      }

    } else {
      window.alert('MemoryToken contract not deployed to detected network.')
    }
    console.log(this.state.memoryToken)
  }

  async loadWeb3() {
    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum)
      await window.ethereum.enable()
    }
    else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider)
    }
    else {
      window.alert('Non-Ethereum browser detected. You should consider trying MetaMask!')
    }
  }

  constructor(props){

    super(props)
      this.state={
          account:'',
          memoryToken: {},
          totalSupply:0,
          tokenURIs:[],
          cardArray:[],
          cardsChosen:[],
          cardsChosenId:[],
          cardsWon:[]
  }
}




  render() {
    return (
      <div>
        <nav className="navbar navbar-dark fixed-top bg-dark flex-md-nowrap p-0 shadow">
          <a
            className="navbar-brand col-sm-3 col-md-2 mr-0"
            href="http://www.dappuniversity.com/bootcamp"
            target="_blank"
            rel="noopener noreferrer"
          >
            ðŸ§  MemorETH Matching Game
          </a>
          <ul className="navbar-nav px-3">
            <li className="nav-item text-nowrap d-none d-sm-none d-sm-block">
              <small className="text-muted"><span id="account">{this.state.account}</span></small>
            </li>
          </ul>
        </nav>
        <div className="container-fluid mt-5">
          <div className="row">
            <main role="main" className="col-lg-12 d-flex text-center">
              <div className="content mr-auto ml-auto">
                <h1 className="d-4">Start matching now!</h1>

                <div className="grid mb-4" >

                  {           }

                </div>

                <div>



                  <div className="grid mb-4" >

                  {            }

                  </div>

                </div>

              </div>

            </main>
          </div>
        </div>


      </div>
    );
  }
}

export default App;
